    public static String FILTER_CLASS_STRING = "";

    public static String LISTENER_CLASS_STRING = "";

    public static String VALVE_CLASS_STRING = "";

    public static String BASIC_FILTER_CLASS_STRING_BASE64 = "";
    public static String BASIC_SEVLET_CLASS_STRING_BASE64 = "";

    public static String CMD_FILTER_CLASS_STRING_BASE64 = "";
